package hello;

public class Bitwiseoperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int m=19;
		int l=35;
		System.out.println("Bitwise AND: " + (m&l));
        System.out.println("Bitwise OR: " + (m|l));
        System.out.println("Bitwise XOR: " + (m^l));
        System.out.println("Bitwise Complement of m:"+(~m));
        System.out.println("Bitwise Complement of l:"+(~l));
	}

}
